mapper.select:
SELECT id
FROM tb_user
WHERE open_id = #{openId}
  AND status = 1;

SELECT DISTINCT p.permission_name
FROM tb_user AS u
         JOIN tb_role AS r ON JSON_CONTAINS(u.role, CAST(r.id AS CHAR))
         JOIN tb_permission AS p ON JSON_CONTAINS(u.role, CAST(p.id AS CHAR))
WHERE u.id = #{userId}
  AND u.`status` = 1;

SELECT d.id AS deptId, d.dept_name AS deptName, u.id AS userId, u.name FROM tb_dept AS d JOIN tb_user AS u ON u.dept_id=d.id WHERE u.status=1
AND u.name LIKE '%${keyword}%'
ORDER BY d.id,u.id;
