mapper.select:
SELECT id
FROM tb_user
WHERE open_id = #{openId}
  AND status = 1;

SELECT DISTINCT p.permission_name
FROM tb_user AS u
         JOIN tb_role AS r ON JSON_CONTAINS(u.role, CAST(r.id AS CHAR))
         JOIN tb_permission AS p ON JSON_CONTAINS(u.role, CAST(p.id AS CHAR))
WHERE u.id = #{userId}
  AND u.`status` = 1;
